mapper.select:
select * from order_ o join orderproduct op on o.odNo = op.odNo join payment p ON o.odNo = p.odNo left join paymentcancle pc ON o.odNO = pc.odNo left join (select mbNo, mbName from member) m on o.mbNo = m.mbNo left join (select nmNo, nmName, nmPhone from nonmember) n on o.nmNo = n.nmNo left join (select m2.mbName as tnName, m2.mbPhone, t2.tnNo from trainerinfo t2 join member m2 on t2.mbNo = m2.mbNo) t on op.tnNo = t.tnNo where o.mbNo = #{mbNo}
AND o.odDate >= #{startDate} AND o.odDate <= DATE_ADD(#{endDate}, INTERVAL 1 DAY)
order by o.odDate DESC limit #{page},#{perPageNum}

select * from order_ o join orderproduct op on o.odNo = op.odNo join payment p ON o.odNo = p.odNo left join paymentcancle pc ON o.odNO = pc.odNo left join (select mbNo, mbName, mbPhone as oderMbPhone from member) m on o.mbNo = m.mbNo left join (select nmNo, nmName, nmPhone from nonmember) n on o.nmNo = n.nmNo left join (select m2.mbName as tnName, m2.mbPhone, t2.tnNo from trainerinfo t2 join member m2 on t2.mbNo = m2.mbNo) t on op.tnNo = t.tnNo where op.tnNo = #{tnNo}
AND o.odDate >= #{startDate} AND o.odDate <= DATE_ADD(#{endDate}, INTERVAL 1 DAY)
order by o.odDate DESC limit #{page},#{perPageNum}

select COUNT(*) from order_ o join orderproduct op on o.odNo = op.odNo join payment p ON o.odNo = p.odNo left join paymentcancle pc ON o.odNO = pc.odNo left join (select mbNo, mbName from member) m on o.mbNo = m.mbNo left join (select nmNo, nmName, nmPhone from nonmember) n on o.nmNo = n.nmNo left join (select m2.mbName as tnName, m2.mbPhone, t2.tnNo from trainerinfo t2 join member m2 on t2.mbNo = m2.mbNo) t on op.tnNo = t.tnNo where op.tnNo = #{tnNo}
AND o.odDate >= #{startDate} AND o.odDate <= DATE_ADD(#{endDate}, INTERVAL 1 DAY)
order by o.odDate DESC

